import React, { useState } from "react";
import "./CustomerSearch.css";
import "bootstrap/dist/css/bootstrap.min.css";
import Container from "react-bootstrap/Container";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import Button from "react-bootstrap/Button";
import { useNavigate } from "react-router-dom";
import axios from "axios";

const LoginPage = () => {
  const [customerCredentials, setCustomerCredentials] = useState({
    firstName: "",
    SSN: "",
  });
  const [employeeCredentials, setEmployeeCredentials] = useState({
    firstName: "",
    SSN: "",
  });
  const [error, setError] = useState("");
  let navigate = useNavigate();

  const handleChange = (event) => {
    const { name, value } = event.target;
    setCustomerCredentials({ ...customerCredentials, [name]: value });
  };
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post(
        "http://localhost:3306/api/login",
        employeeCredentials
      );
      console.log(response.data.message);
      // after successful login
    } catch (err) {
      setError(err.response.data.error);
    }
  };

  return (
    <div>
      <Container>
        <Row>
          <Col>
            <form onSubmit={handleSubmit}>
              <h1>Customer Login </h1>
              <label>
                First Name:
                <input
                  type="name"
                  name="cusfirstName"
                  value={customerCredentials.firstName}
                  onChange={handleChange}
                />
              </label>
              <label>
                SSN:
                <input
                  type="SSN"
                  name="cusSSN"
                  value={customerCredentials.SSN}
                  onChange={handleChange}
                />
              </label>
              <Button
                onClick={() => {
                  navigate("/EmployeeSearch");
                }}
              >
                EmployeePage
              </Button>
              <Button
                onClick={() => {
                  navigate("/CustomerSearch");
                }}
              >
                CustomerPage
              </Button>
              <Button type="submit">Submit</Button>
            </form>
          </Col>

          <Col>
            <form onSubmit={handleSubmit}>
              <h1>Employee Login</h1>
              <label>
                First Name:
                <input
                  type="name"
                  name="empfirstName"
                  value={employeeCredentials.firstName}
                  onChange={handleChange}
                />
              </label>
              <label>
                SSN:
                <input
                  type="SSN"
                  name="empSSN"
                  value={employeeCredentials.SSN}
                  onChange={handleChange}
                />
              </label>
              <Button
                onClick={() => {
                  navigate("/EmployeeSearch");
                }}
              >
                EmployeePage
              </Button>
              <Button
                onClick={() => {
                  navigate("/CustomerSearch");
                }}
              >
                CustomerPage
              </Button>
              <Button type="submit">Submit</Button>
            </form>
          </Col>
        </Row>
      </Container>
    </div>
  );
};

export default LoginPage;
